procedure quickSort(ref int[] arr, int from, int to)
    int partitionIndex;
begin
     if (from < to) then
        partition(arr, partitionIndex, from, to);
        quickSort(arr, from, partitionIndex - 1);
        quickSort(arr, partitionIndex + 1, to);
     end;
end;
procedure partition(ref int[] arr, ref int partitionIndex, int from, int to)
    int pivot;
    int i;
    int j;
    int swapTemp;
begin
    pivot = arr[to];
    i = from - 1;
    j = from;
    while (j < to) do
        if (arr[j] <= pivot) then
            i = i + 1;
            swapTemp = arr[i];
            arr[i] = arr[j];
            arr[j] = swapTemp;
        end;
        j = j + 1;
    end;
    swapTemp = arr[i + 1];
    arr[i + 1] = arr[to];
    arr[to] = swapTemp;
    partitionIndex = i + 1;
end;
